var mongoose = require('mongoose');


var Schema = mongoose.Schema;

/**
 * status: Applied, Rejected, Approved
 */

var applicationSchema = new Schema({
    character: String,
    realm: String,
    previousGuild: String,    
    role: String,
    comments: String,
    desiredRole: String,
    dateApplied: Date,
    guild: String,
    status: String    
})

module.exports = mongoose.model('applications', applicationSchema);