var mongoose = require('mongoose');


var Schema = mongoose.Schema;

var forumSchema = new Schema({
    
    name: String,
    categoryId: String, 
    threadCount: Number,
    threads: Array,
    permissions: [
        {
            raider: Boolean,
            officer: Boolean,
            minRank: Number,
            public: Boolean
        }
    ] 
})

module.exports = mongoose.model('forums', forumSchema);