var mongoose = require('mongoose');


var Schema = mongoose.Schema;
var GuildSchema = new Schema({
    name: String,
    members: [
        {
            user: String,
            rank: Number,
            officer: Boolean,
            raider: Boolean,
            GM: Boolean,
            approved: Boolean
        }
    ],
    tabs: [
        {
            title: String,
            content: String
        }
    ],
    ranks: [
        {
            name: String,
            raider: Boolean,
            officer: Boolean,
            rank: Number
        }
    ],
    images: Array,
    bannerImage: String,
    logsLink: String,
    progressionlink: String
})
 
module.exports = mongoose.model('guilds', GuildSchema);